SELECT public.user_group.group_id,
public."group".restaurant_id,
public."restaurant".restaurant_name,
public."restaurant".restaurant_image,
public."group".group_init_user_id,
public."user".user_id, 
public."user".username, 
public."user".gender, 
public."user".user_photo
FROM public.user_group
INNER JOIN public."user"
    ON public.user_group.user_id = public."user".user_id
INNER JOIN public."group"
    ON public.user_group.group_id = public."group".group_id
INNER JOIN public.restaurant
    ON public."group".restaurant_id = public."restaurant".restaurant_id
WHERE public.user_group.user_id = $id AND public."group".group_meeting_timestamp > NOW();

